mapper.insert:
INSERT INTO tbl_member
(
  userid
  , userpw
  , username
  , regdate
  , updatedate
  , enabled
  , birthday
, height
, weight
, tel
, sex
)
VALUES
(
  #{userId}
  , #{userPw}
  , #{userName}
  , NOW()
  , NOW()
  , 'Y'
  , #{birthday}
  , #{height}
  , #{weight}
  , #{tel}
  , #{sexCd}
)

mapper.update:
UPDATE	tbl_member
SET	userpw = #{userPw}

  , username = #{userName}


  , enabled = #{enabled}

  , updatedate = NOW()
WHERE	userid = #{userId}

mapper.insert:
INSERT INTO tbl_member_auth
(
  userid
  , auth
)
VALUES
(
  #{userId}
  , #{auth}
)

mapper.update:
UPDATE	tbl_member_auth
SET		auth = #{auth}
WHERE	userid = #{userId}

mapper.select:
SELECT COUNT(*)
  FROM tbl_member
 WHERE userid = #{userId}
