mapper.update:
UPDATE	tbl_member
SET		userpw = #{userPw}
    , username = #{userName}
    , updatedate = NOW()
    , enabled = #{enabled}
WHERE	userid = #{userId}

mapper.insert:
INSERT INTO tbl_member_auth
(
  userid
  , auth
)
VALUES
(
  #{userId}
  , #{auth}
)

mapper.update:
UPDATE	tbl_member_auth
SET		auth = #{auth}
WHERE	userid = #{userId}
