mapper.select:
/* com.ltop.app.menu5.mapper.M5Sub1Mapper.selectUserList */ SELECT mem.userid AS userId , mem.userpw , mem.username AS userName , mem.enabled , mem.birthday , mem.mat_id AS matId , mem.height , mem.weight , mem.tel , mem.reguserid AS reguserId , mem.agency_no AS agencyNo , (SELECT agency_name FROM tbl_agency WHERE agency_no = mem.agency_no) AS agencyName , (SELECT adm_id FROM tbl_agency WHERE agency_no = mem.agency_no) AS admId , auth.auth , mem.regdate AS regDate , mem.updatedate AS updateDate FROM tbl_member mem LEFT OUTER JOIN tbl_member_auth auth ON auth.userid = mem.userid AND auth.auth = 'ROLE_USER' WHERE 1=1
AND mem.username LIKE CONCAT('%',#{searchUserName},'%')
AND mem.mat_id LIKE CONCAT('%',#{searchMatId},'%')
AND mem.enabled = #{searchEnabled}
AND mem.agency_no = #{searchAgency}
ORDER BY mem.regdate DESC

mapper.insert:
INSERT INTO tbl_member
(
    userid
   , userpw
   , username
   , enabled
   , birthday
   , mat_id
   , height
   , weight
   , tel
   , reguserid
   , agency_no
   , regdate
   , updatedate
)
VALUES
(
    #{userId}
  , #{userPw}
  , #{userName}
  , #{enabled}
  , #{birthday}
  , #{matId}
  , #{height}
  , #{weight}
  , #{tel}
  , #{reguserId}
  , #{agencyNo}
  , NOW()
  , NOW()
)

mapper.update:
UPDATE	tbl_member
SET		username = #{userName}
    , updatedate = NOW()
    , enabled = #{enabled}
    , tel = #{tel}
    , mat_id = #{matId}
WHERE	userid = #{userId}

mapper.insert:
INSERT INTO tbl_member_auth
(
  userid
  , auth
)
VALUES
(
  #{userId}
  , #{auth}
)
