mapper.select:
SELECT COUNT(*) FROM tbl_member mem LEFT OUTER JOIN tbl_member_auth auth ON auth.userid = mem.userid AND auth.auth = 'ROLE_USER' WHERE 1=1
AND (mem.userid <> 'admin' OR auth.auth <> 'ROLE_ADMIN')
AND mem.username LIKE CONCAT('%',#{searchUserName},'%')
AND mem.mat_id LIKE CONCAT('%',#{searchMatId},'%')
AND mem.enabled = #{searchEnabled}
AND mem.agency_no = #{searchAgency}

/* com.ltop.app.menu5.mapper.M5Sub1Mapper.selectUserList */
SELECT R.* FROM ( SELECT mem.userid AS userId , mem.userpw , mem.username AS userName , mem.sex AS sexName , mem.sleep_time AS sleepTime , mem.position_time AS positionTime , mem.enabled , mem.birthday , mem.mat_id AS matId , mem.height , mem.weight , mem.tel , mem.reguserid AS reguserId , mem.agency_no AS agencyNo , (SELECT agency_name FROM tbl_agency WHERE agency_no = mem.agency_no) AS agencyName , (SELECT adm_id FROM tbl_agency WHERE agency_no = mem.agency_no) AS admId , auth.auth , mem.regdate AS regDate , mem.updatedate AS updateDate FROM tbl_member mem LEFT OUTER JOIN tbl_member_auth auth ON auth.userid = mem.userid AND auth.auth = 'ROLE_USER' WHERE 1=1
AND mem.username LIKE CONCAT('%',#{m5Sub1VO.searchUserName},'%')
AND mem.mat_id LIKE CONCAT('%',#{m5Sub1VO.searchMatId},'%')
AND mem.enabled = #{m5Sub1VO.searchEnabled}
AND mem.agency_no = #{m5Sub1VO.searchAgency}
) R WHERE 1=1 AND (R.userId <> 'admin' OR R.auth <> 'ROLE_ADMIN') ORDER BY R.regdate DESC LIMIT #{pageVO.amount} OFFSET #{pageVO.startNum}

