mapper.update:
UPDATE	tbl_member
   SET	username = #{userName}

           , userpw = #{userPw}

       , sex = #{sexCd}
       , enabled = #{enabled}
       , tel = #{tel}
       , birthday = #{birthday}
       , height = #{height}
       , weight = #{weight}
       , mat_id = #{matId}
       , agency_no = #{agencyNo}
       , sleep_time = #{sleepTime}
       , position_time = #{positionTime}
       , updatedate = NOW()
   WHERE userid = #{userId}

