mapper.select:
SELECT A.userid AS userId , A.username AS userName , B.seq , IFNULL(B.tel, A.tel) AS tel , B.use_yn AS useYn , B.regdate AS regDate FROM tbl_member A LEFT OUTER JOIN tbl_alarm B ON B.userid = A.userid WHERE 1=1 AND A.userid = #{userId}
AND B.seq = #{seq}
ORDER BY B.seq DESC LIMIT 1

mapper.insert:

            SELECT MAX(seq)+1 FROM tbl_alarm WHERE userid= #{userId}


