mapper.select:
SELECT	COUNT(*) 
FROM 	tbl_mat_info 
WHERE 	1 = 1

		
		AND mat_id = #{searchMatId}
		
		
		
		AND use_yn = #{searchUseYn}

SELECT R.* FROM ( SELECT mat_no AS matNo , mat_id AS matId , description , use_yn AS useYn , attr1 , attr2 , attr3 , attr4 , attr5 , attr6 , attr7 , attr8 , attr9 , attr10 , regid AS regId , reg_date AS regDate FROM tbl_mat_info WHERE 1 = 1
AND mat_id = #{m5Sub2VO.searchMatId}
AND use_yn = #{m5Sub2VO.searchUseYn}
) R ORDER BY R.matNo DESC LIMIT #{pageVO.amount} OFFSET #{pageVO.startNum}

mapper.update:
UPDATE	tbl_mat_info 
SET		description = #{description}
		, use_yn = #{useYn}
WHERE	mat_no = #{matNo}
