mapper.select:
		SELECT	COUNT(*)
		FROM 	tbl_mat_info
		WHERE 	1 = 1

				
				AND mat_id = #{searchMatId}
				
				
                AND A.description  LIKE CONCAT('%',#{searchMatDesc},'%')
                
                
                AND A.agency_no = #{searchAgency}
                
				
				AND use_yn = #{searchUseYn}

SELECT R.* FROM ( SELECT A.mat_no AS matNo , A.mat_id AS matId , A.description , A.use_yn AS useYn , A.group_seq AS groupSeq , A.agency_no AS agencyNo , B.agency_name AS agencyName , B.group_name AS groupName , B.group_adm_id AS groupAdmId , (SELECT username FROM tbl_member WHERE userid = B.group_adm_id) AS groupAdmName , A.attr1 , A.attr2 , A.attr3 , A.attr4 , A.attr5 , A.attr6 , A.attr7 , A.attr8 , A.attr9 , A.attr10 , A.regid AS regId , A.reg_date AS regDate FROM tbl_mat_info A LEFT OUTER JOIN ( SELECT TAC.agency_no , TAC.agency_name , TAC.description AS agencyDesc , TAC.adm_id , TAG.group_seq , TAG.group_name , TAG.description AS groupDesc , TAG.group_adm_id FROM tbl_agency TAC LEFT OUTER JOIN tbl_agency_group TAG ON TAG.agency_no = TAC.agency_no AND TAG.use_yn = 'Y' WHERE 1=1 AND TAC.use_yn = 'Y' ) B ON B.agency_no = A.agency_no
WHERE 1 = 1
AND A.mat_id = #{m5Sub2VO.searchMatId}
AND A.description LIKE CONCAT('%',#{m5Sub2VO.searchMatDesc},'%')
AND A.agency_no = #{m5Sub2VO.searchAgency}
AND A.use_yn = #{m5Sub2VO.searchUseYn}
) R ORDER BY R.matNo DESC LIMIT #{pageVO.amount} OFFSET #{pageVO.startNum}

mapper.update:
UPDATE	tbl_mat_info
   SET	description = #{description}
		, use_yn = #{useYn}
		, agency_no = #{agencyNo}
		, group_seq = #{groupSeq}
WHERE	mat_no = #{matNo}
