mapper.select:
SELECT	COUNT(*) 
FROM	tbl_agency ta, tbl_member tm
WHERE	ta.adm_id = tm.userid


		AND ta.agency_name = #{searchAgencyName}



		AND tm.username = #{searchAdmName}

SELECT R.* FROM ( SELECT ta.agency_no , ta.agency_name , ta.description , ta.adm_id , tm.username AS admName , ta.use_yn , ta.reg_date , ( SELECT COUNT(*) FROM tbl_agency_math WHERE agency_no = ta.agency_no
AND mat_id = #{m5Sub3VO.searchMatId}
) AS agencyMatCount FROM tbl_agency ta, tbl_member tm WHERE ta.adm_id = tm.userid
AND ta.agency_name = #{m5Sub3VO.searchAgencyName}
AND tm.username = #{m5Sub3VO.searchAdmName}
) R ORDER BY R.agency_no DESC LIMIT #{pageVO.amount} OFFSET #{pageVO.startNum}

mapper.insert:

	SELECT MAX(agency_no)+1 FROM tbl_agency


INSERT INTO tbl_agency 
(
	agency_no
	, agency_name
	, description
	, adm_id
	, use_yn
	, reg_date
)
VALUES 
(
	IFNULL(#{agencyNo},1)
	, #{agencyName}
	, #{description}
	, #{admId}
	, #{useYn}
	, NOW()
)

mapper.update:
UPDATE	tbl_agency 
SET		description = #{description}
		, adm_id = #{admId}
		, use_yn = #{useYn}
WHERE	agency_no = #{agencyNo}

mapper.insert:
INSERT INTO tbl_agency_math
(
	agency_no
	, mat_id
	, description
	, use_yn
	, reg_date
	, userid
)
VALUES 
(
	#{agencyNo}
	, #{matId}
	, #{description}
	, #{useYn}
	, NOW()
	, #{userId}
)

mapper.update:
UPDATE	tbl_agency_math 
SET		description = #{description}
		, use_yn = #{useYn}
		, userid = #{userId}
WHERE	agency_no = #{agencyNo}
		AND mat_id = #{matId}

mapper.delete:
DELETE FROM tbl_agency_math 
WHERE agency_no = #{agencyNo} 


AND mat_id = #{matId}
