mapper.select:
SELECT R.* FROM (
SELECT tag.group_seq AS groupSeq , tag.agency_no AS agencyNo , tag.group_name AS groupName , tag.description AS description , tag.group_adm_id AS groupAdmId , tag.use_yn AS useYn , tag.reg_date AS regDate , tac.agency_name AS agencyName , tm.username AS groupAdmName , ( SELECT COUNT(*) FROM tbl_mat_info WHERE agency_no = tag.agency_no ) AS agencyMatCnt , ( SELECT COUNT(*) FROM tbl_member WHERE agency_no = tag.agency_no ) AS agencyUserCnt FROM tbl_agency_group tag INNER JOIN tbl_agency tac ON tac.agency_no = tag.agency_no LEFT OUTER JOIN tbl_member tm ON tm.userid = tag.group_adm_id WHERE 1=1
AND tac.agency_name LIKE CONCAT('%',#{m5Sub5VO.searchAgencyName},'%')
AND tag.group_name LIKE CONCAT('%',#{m5Sub5VO.searchGroupName},'%')
AND tm.username LIKE CONCAT('%',#{m5Sub5VO.searchAdmName},'%')
) R ORDER BY R.agencyNo DESC LIMIT #{pageVO.amount} OFFSET #{pageVO.startNum}

mapper.insert:

  SELECT MAX(agency_no)+1 FROM tbl_agency


INSERT INTO tbl_agency
(
  agency_no
  , agency_name
  , description
  , adm_id
  , use_yn
  , reg_date
)
VALUES
(
  IFNULL(#{agencyNo},1)
  , #{agencyName}
  , #{description}
  , #{admId}
  , #{useYn}
  , NOW()
)

mapper.update:
UPDATE	tbl_agency
   SET agency_name = #{agencyName}
    , description = #{description}
    , adm_id = #{admId}
    , use_yn = #{useYn}
WHERE	agency_no = #{agencyNo}

mapper.insert:
INSERT INTO tbl_agency_math
(
  agency_no
  , mat_id
  , description
  , use_yn
  , reg_date
  , userid
)
VALUES
(
  #{agencyNo}
  , #{matId}
  , #{description}
  , #{useYn}
  , NOW()
  , #{userId}
)

mapper.update:
UPDATE	tbl_agency_math
SET		description = #{description}
    , use_yn = #{useYn}
    , userid = #{userId}
WHERE	agency_no = #{agencyNo}
    AND mat_id = #{matId}

mapper.delete:
DELETE FROM tbl_agency_math
WHERE agency_no = #{agencyNo}


AND mat_id = #{matId}
